import React, { Component } from "react";
import Dialog from "@material-ui/core/Dialog";
import AppBar from "@material-ui/core/AppBar";
import {
  ThemeProvider as MuiThemeProvider,
  createMuiTheme,
} from "@material-ui/core/styles";
import { List, ListItem, ListItemText } from "@material-ui/core/";
import Button from "@material-ui/core/Button";
import { profile } from "../../utils/api.js";
import { Link } from "react-router-dom";
import Icon from "@material-ui/core/Icon";
import TypoGraphy from "@material-ui/core/Typography";
import Toolbar from "@material-ui/core/Toolbar";
// import Nav from "../UserNav";
import { grey } from "@material-ui/core/colors";
import { withStyles } from '@material-ui/core/styles';

const theme = createMuiTheme({
  palette: {
    primary: {
      main: "#17A2b8",
    },
    secondary: {
      main: grey[900],
    },
  },
});

export class Confirm extends Component {
  continue = (e) => {
    e.preventDefault();
    // PROCESS FORM //
    //let history = useHistory();
    // let history = useHistory();
    const {
      values: {
        navColor,
        navText,
        navImage,
        introText,
        introTitle,
        bio,
        aboutBgColor,
        profileImage,
        ProjectOneTitle,
        ProjectOneText,
        ProjectOneUrl,
        ProjectTwoTitle,
        ProjectTwoText,
        ProjectTwoUrl,
        ProjectThreeTitle,
        ProjectThreeText,
        ProjectThreeUrl,
        resumeUrl,
        githubLink,
        linkdin,
        footer,
      },
    } = this.props;
    // console.log(this.props);
    // console.log(this.props.values.firstName);
    // console.log(this.props.values.lastName);
    // console.log(this.props.values.email);
    // console.log(this.props.values.occupation);
    // console.log(this.props.values.city);
    // console.log(this.props.values.bio);

    const newUser = {
      navColor: navColor,
      navText: navText,
      navImage: navImage,
      introText: introText,
      introTitle: introTitle,
      bio: bio,
      aboutBgColor: aboutBgColor,
      profileImage: profileImage,
      ProjectOneTitle: ProjectOneTitle,
      ProjectOneText: ProjectOneText,
      ProjectOneUrl: ProjectOneUrl,
      ProjectTwoTitle: ProjectTwoTitle,
      ProjectTwoText: ProjectTwoText,
      ProjectTwoUrl: ProjectTwoUrl,
      ProjectThreeTitle: ProjectThreeTitle,
      ProjectThreeText: ProjectThreeText,
      ProjectThreeUrl: ProjectThreeUrl,
      resumeUrl: resumeUrl,
      githubLink: githubLink,
      linkdin: linkdin,
      footer: footer,
    };
    //we can do this also
    //   const newUser = {
    //   firstName: this.props.values.firstName,
    // email: this.props.values.email,
    // lastName: this.props.values.lastName,
    // occupation: this.props.values.occupation,
    // city:this.props.values.city,
    // bio: this.props.values.bio,
    // }

    profile(newUser).then(function (res) {
      // let history = useHistory();
      // return history.push("/mainprofile")
      // if (res) {
      //   this.props.history.push(`/mainprofile`)
      // }
      // console.log(res);
    });

    this.props.nextStep();
  };

  back = (e) => {
    e.preventDefault();
    this.props.prevStep();
  };

  render() {
    const {
      values: {
        navColor,
        navText,
        navImage,
        introText,
        introTitle,
        bio,
        aboutBgColor,
        profileImage,
        ProjectOneTitle,
        ProjectOneText,
        ProjectOneUrl,
        ProjectTwoTitle,
        ProjectTwoText,
        ProjectTwoUrl,
        ProjectThreeTitle,
        ProjectThreeText,
        ProjectThreeUrl,
        resumeUrl,
        githubLink,
        linkdin,
        footer,
      },
    } = this.props;

    const StyledButton = withStyles({
      root: {
        background: 'linear-gradient(45deg, #FE6B8B 30%, #FF8E53 90%)',
        borderRadius: 3,
        border: 0,
        color: 'white',
        height: 36,
        size: 'large',
        padding: '0 30px',
